package ch04.sec05;

public class Printfiom1To10Ex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("1부터 100까지의 합을 구하는  while문");
System.out.println("================================================");

		int i =1;
while (i<=10) {
	System.out.println(i + " ");
	i++;
	
}
System.out.println("이클립스 줄맞추기 단축키는 지피티에 물어보면 된다.");
	}

}
